import React,{useState,useEffect} from 'react';
import { connect } from 'react-redux';
import PostList from './PostList';
import PostCreate from './PostCreate';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Router, Route} from 'react-router-dom';
import { resetSuccessMessage } from '../actions';
import history from '../history';
import PostEdit from './PostEdit';
import Header from './Header';
import { GoogleLogin, GoogleLogout } from 'react-google-login';
import { gapi } from 'gapi-script';
const toastConfig = {
  position: 'top-center',
  autoClose: 2000,
  hideProgressBar: false,
  closeOnClick: true,
  pauseOnHover: true,
  draggable: true,
  progress: undefined,
  theme: 'dark',
};

const App = ({resetSuccessMessage, posts}) => {

  const [ profile, setProfile ] = useState([]);
  const [isLogin, setIslogin]=useState(false);
 const clientId = '260034014064-t9k3lhrlke6ocfvt1d69r6nddktpqk34.apps.googleusercontent.com';

 useEffect(() => {
  const initClient = () => {
        gapi.client.init({
        clientId: clientId,
        scope: ''
      });
   };
   gapi.load('client:auth2', initClient);
});

const onSuccess = (res) => {
  setIslogin(true);
  setProfile(res.profileObj);
};

const onFailure = (err) => {
  console.log('failed', err);
};

const logOut = () => {
  setIslogin(false);
  setProfile(null);
};



 useEffect(() => {
    if (posts.successMessage) {
      toast.success(posts.successMessage, toastConfig);
      resetSuccessMessage();
    }
  }, [posts.successMessage]);
  return (
 <div>
  < Header/>
 <h2>React Google Login</h2>
    <br />
    <br />
    {isLogin ? (
        <div>
            <img src={profile.imageUrl} alt="user image" />
            <h3>User Logged in</h3>
            <p>Name: {profile.name}</p>
            <p>Email Address: {profile.email}</p>
            <br />
            <br />
            <GoogleLogout clientId={clientId} buttonText="Log out" onLogoutSuccess={logOut} />
        </div>
    ) : (
        <GoogleLogin
            clientId={clientId}
            buttonText="Sign in with Google"
            onSuccess={onSuccess}
            onFailure={onFailure}
            cookiePolicy={'single_host_origin'}
            isSignedIn={()=> setIslogin(true)}
        />
    )}
      <ToastContainer />
      <Router history={history}>
        < Route  path="/" />
     <Route path="/add" component={PostCreate} />
        <Route path="/" exact  component={PostList} />
        <Route path="/edit/:id"   component={PostEdit} />
      </Router>
    </div>
  );
};
const mapStateToProps = (state) => {
  // console.lo
  return { posts: state.posts };
};
export default connect(mapStateToProps, { resetSuccessMessage })(App);
