import React, { useEffect, useState } from 'react';
import _ from 'lodash';
import { connect } from 'react-redux';
import { addPost, cancelForm } from '../actions';

import Form from './Form';

const PostList = ({ posts, addPost, cancelForm }) => {
  const onFormSubmit = (data) => {
    addPost({
      title: data.title,
      body: data.description,                               //adding submitted data to json-server
      createdAt: new Date().getTime(),
    });
  };

  return (
    <div>
      <div className="header d-flex justify-content-between align -item-center m-2">
        <h1> CREATE POST</h1>
      </div>
      <Form
        onFormSubmit={onFormSubmit}
        selectedPostDetails={posts.selectedPostDetails}
        onCancel={cancelForm}                                             //input form
        onSumbitLoading={posts.addPostLoading}
      />
    </div>
  );
};

const mapStateToProps = (state) => {
  // console.log('state', state);
  return { posts: state.posts };
};

export default connect(mapStateToProps, {
  addPost,
  cancelForm,
})(PostList);
