import React, { useEffect, useState } from 'react';
import _ from 'lodash';
import DataTable from 'react-data-table-component';
import { connect } from 'react-redux';
import { setPosts, showForm, addPost, getPost } from '../actions';
import moment from 'moment/moment';
import Form from './Form';

const PostList = ({ posts, setPosts, showForm, addPost, getPost }) => {
  useEffect(() => {
    setPosts();
  }, []);



  const columns = [
    {
      name: 'ID',
      selector: (row) => row.id,
    },
    {
      name: 'Title',
      selector: (row) => row.title,
      sortable: true,
    },
    {
      name: 'Description',
      selector: (row) => row.body,
    },
    {
      name: 'Created Date',
      selector: (row) => moment(row.createdAt).format('DD-MM-YYYY'),
    },
    {
      name: 'Action',
      selector: (row) => (
        <div>
          <button
            type="button"
            className="btn btn-warning"
            onClick={() => onEditRecord(row)}
          >
            Edit
            {posts.onEditLoading && posts.selectedPostId === row.id ? <i className="bi bi-arrow-clockwise"></i> : null} 
          </button>
        </div>
      ),
    },
  ];
  
  const onEditRecord = (row) => {
    getPost(row.id);
  };
  

  const onFormSubmit = (data) => {
    addPost({
      title: data.title,
      body: data.description,
      createdAt: new Date().getTime(),
    });
  };
  return (
    <div>
      <div className="header d-flex justify-content-between align -item-center m-2">
        <h1>POSTS</h1>
        <button
          type="button"
          className="btn btn-primary"
          onClick={() => showForm(true)}
        >
          Add Post
          {posts.addPostLoading ? <i className="bi bi-arrow-clockwise"></i> : null} 
        </button>
      </div>
      {posts.showForm ? (
        <Form onFormSubmit={onFormSubmit} />
      ) : (
        <DataTable
          columns={columns}
          data={posts.data}
          pagination
          progressPending={posts.loadingData}
        />
      )}
    </div>
  );
};

const mapStateToProps = (state) => {
  // console.log('state', state);
  return { posts: state.posts };
};

export default connect(mapStateToProps, {
  setPosts,
  showForm,
  addPost,
  getPost,
})(PostList);
