import React, { useEffect } from 'react';
import formik, { useFormik } from 'formik';

const Form = ({ onFormSubmit, selectedPostDetails, onCancel, onSumbitLoading }) => {
  // console.log(selectedPostDetails);
  const validate = (values) => {
    const errors = {};
    if (!values.title) {
      errors.title = 'Title Required';
    }

    if (!values.description) {
      errors.description = 'Description Required';
    }
    return errors;
  };

  const formik = useFormik({
    initialValues: {
      title: selectedPostDetails ? selectedPostDetails.title : '',
      description: selectedPostDetails ? selectedPostDetails.description : '',
    },
    validate,
    onSubmit: (values) => {
      onFormSubmit(values);
    },
  });

  useEffect(() => {
    if (selectedPostDetails) {
      formik.setValues({ title: selectedPostDetails.title });
      formik.setValues({ description: selectedPostDetails.body });
    }
  }, []);

  return (
    <div style={{ margin: '50px 50px 50px 50px' }}>
      <form onSubmit={formik.handleSubmit}>
        <div className="form-group">
          <label>Title</label>
          <input
            type="text"
            className="form-control"
            placeholder="title"
            name="title"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.title}
          />
        </div>
        <p>
          {' '}
          {formik.touched.title && formik.errors.title ? (
            <span>{formik.errors.title}</span>
          ) : null}
        </p>
        <div className="form-group">
          <label>Description</label>
          <textarea
            className="form-control"
            rows="3"
            name="description"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.description}
          ></textarea>
          <p>
            {' '}
            {formik.touched.description && formik.errors.description ? (
              <span>{formik.errors.description}</span>
            ) : null}
          </p>
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
          {on ? <i className="bi bi-ubuntu"></i> : null}
        </button>
        <button type="button" className="btn btn-light" onClick={onCancel}>
          Cancel
        </button>
      </form>
    </div>
  );
};

export default Form;
