import React from 'react'
import { Redirect, Route } from 'react-router'
import { fakeAuth } from '../Auth/FakeAuth';

export default function PrivateRoute({ children, ...rest }) {

    return (
      <Route
        {...rest}
        render={({ location }) =>
           fakeAuth.isAuthenticated ? (
            children
          ) : (
            <Redirect
              to={{
                pathname: "/",
                state: { from: location }
              }}
            />
          )
        }
      />
    );
  }
