import React from 'react';

import { Route, Redirect } from 'react-router-dom';

const PrivateRoute = ({ isAuthenticated, ...rest }) => {
  if (!isAuthenticated) {
    return (
      <Redirect
        to={{
          pathname: '/',
        }}
      />
    );
  }
  return <Route {...rest} render={(...rest) => children} />;
};

export default PrivateRoute;
