import React, { useEffect, useState } from 'react';
import _ from 'lodash';

import { connect } from 'react-redux';
import { addPost, cancelForm } from '../actions';
import moment from 'moment/moment';
import Form from './Form';

const PostList = ({ posts, addPost, cancelForm }) => {
  const onFormSubmit = (data) => {
    addPost({
      title: data.title,
      body: data.description,
      createdAt: new Date().getTime(),
    });
  };

  return (
    <div>
      <div className="header d-flex justify-content-between align -item-center m-2">
        <ToastContainer />
        <h1>POSTS</h1>



        {posts.showForm ? null : (
          <button
            type="button"
            className="btn btn-primary"
            onClick={() => showForm(true)}
          >
            Add Post
          </button>
        )}
      </div>
      {posts.showForm ? (
        <Form
          onFormSubmit={onFormSubmit}
          selectedPostDetails={posts.selectedPostDetails}
          onCancel={cancelForm}
          onSumbitLoading={posts.addPostLoading}
        />
      ) : (
        <DataTable
          columns={columns}
          data={posts.data}
          pagination
          progressPending={posts.loadingData}
        />
      )}
    </div>
  );
};

const mapStateToProps = (state) => {
  // console.log('state', state);
  return { posts: state.posts };
};

export default connect(mapStateToProps, {
  addPost,

  cancelForm,
})(PostList);
