import React from 'react';
import { connect } from 'react-redux';
import PostList from './PostList';
import PostCreate from './PostCreate';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { BrowserRouter, Route, Link } from 'react-router-dom';
import { resetSuccessMessage } from '../actions';

const toastConfig = {
  position: 'top-center',
  autoClose: 2000,
  hideProgressBar: false,
  closeOnClick: true,
  pauseOnHover: true,
  draggable: true,
  progress: undefined,
  theme: 'dark',
};

const App = (resetSuccessMessage) => {
  return (
    <div>
      <BrowserRouter>
        <Route path="/add" component={PostCreate} />
        <Route path="/list" exact component={PostList} />
      </BrowserRouter>
    </div>
  );
};
const mapStateToProps = (state) => {
  // console.lo
  return {};
};
export default connect(mapStateToProps,{resetSuccessMessage})(App);
